async function sha256(message) {const msgUint8 = new TextEncoder().encode(message);const hashBuffer = await crypto.subtle.digest('SHA-256', msgUint8);const hashArray = new Uint8Array(hashBuffer);let base64String = btoa(String.fromCharCode(...hashArray));return base64String;}function checkpass(usrinput, password_b64_sha256) {return sha256(usrinput) === password_b64_sha256;}